  background: rgba(59, 130, 246, 0.08);
}

.status-chip {
  padding: 0.2rem 0.75rem;
  border-radius: 999px;
  font-size: 0.85rem;
  font-weight: 600;
}

.status-chip.shortlisted {
  background: rgba(16, 185, 129, 0.12);
  color: #059669;
}

.status-chip.entrevista {
  background: rgba(59, 130, 246, 0.1);
  color: var(--clr-primary);
}

.status-chip.hold {
  background: rgba(245, 158, 11, 0.12);
  color: #b45309;
}

.empty-cell {
  text-align: center;
  padding: 2rem 0;
  color: var(--clr-muted);
}

.detail-block {
  margin-bottom: 1.2rem;
}

.detail-label {
  text-transform: uppercase;
  letter-spacing: 0.1em;
  font-size: 0.75rem;
  color: var(--clr-muted);
}

.detail-value {
  font-size: 1rem;
  margin: 0.2rem 0 0;
}

.detail-actions {
  display: flex;
  gap: 0.5rem;
  flex-wrap: wrap;
  align-items: center;
}

@media (max-width: 960px) {
